mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID = #{checkId}

mapper.insert:
INSERT 
	INTO MEMBER
	(
			USER_NO
		, USER_ID
		, USER_PWD
		, USER_NAME
		, EMAIL
		, GENDER
		, PHONE
		, ADDRESS
	)
	VALUES
	(
		SEQ_UNO.NEXTVAL
	, #{userId}
	, #{userPwd}
	, #{userName}
	, #{email}
	, #{gender}
	, #{phone}
	, #{address}
	)

mapper.update:
UPDATE MEMBER
SET PROFILE_URL = #{profileUrl}
  , MODIFY_DATE = SYSDATE
WHERE USER_ID = #{userId}

UPDATE MEMBER
SET	USER_NAME = #{userName} 
	,	EMAIL = #{email}
	, PHONE = #{phone}
	, ADDRESS = #{address}
	, GENDER = #{gender}
	, MODIFY_DATE = SYSDATE
WHERE USER_ID = #{userId}

UPDATE MEMBER
SET STATUS = 'N'
WHERE USER_ID = #{userId}
